mapper.select:
SELECT COUNT(*)
FROM attendance a
JOIN employee e ON a.emp_no = e.emp_no
WHERE (#{empNo} IS NULL OR a.emp_no = #{empNo})

      AND a.attendance_date = #{attendanceDate}


      AND e.dept_code = #{deptCode}


      AND d.division_code = #{divisionCode}


      AND e.emp_name LIKE CONCAT('%', #{empName}, '%')

SELECT a.emp_no AS empNo, e.emp_name AS empName, a.attendance_date AS attendanceDate, a.attendance_status AS attendanceStatus, a.clock_in_time AS clockInTime, a.clock_out_time AS clockOutTime, a.create_datetime AS createDatetime, a.update_datetime AS updateDatetime, d.dept_name AS deptName, dv.division_name AS divisionName FROM attendance a JOIN employee e ON a.emp_no = e.emp_no JOIN department d ON e.dept_code = d.dept_code JOIN division dv ON d.division_code = dv.division_code WHERE (#{empNo} IS NULL OR a.emp_no = #{empNo})
AND a.attendance_date = #{attendanceDate}
AND e.dept_code = #{deptCode}
AND d.division_code = #{divisionCode}
AND e.emp_name LIKE CONCAT('%', #{empName}, '%')
ORDER BY a.attendance_date DESC LIMIT #{limit} OFFSET #{offset};

mapper.update:
UPDATE attendance
SET 
	clock_out_time = #{clockOutTime}
	,attendance_status = #{attendanceStatus}
WHERE emp_no = #{empNo}

mapper.select:
SELECT attendance_date AS attendanceDate , emp_no AS empNo , attendance_status AS attendanceStatus , clock_in_time AS clockInTime , clock_out_time AS clockOutTime FROM attendance WHERE emp_no = #{empNo}
AND attendance_status = #{attendanceStatus}
ORDER BY attendanceDate DESC
