mapper.select:
SELECT customer_no AS customerNo, customer_name AS customerName, postal_code AS postalCode, basic_address AS basicAddress, detail_address AS detailAddress, customer_phone AS customerPhone, create_datetime AS createDatetime, update_datetime AS updateDatetime FROM customer WHERE 1=1
AND (customer_name LIKE CONCAT('%', #{searchWord}, '%') OR customer_phone LIKE CONCAT('%', #{searchWord}, '%'))
LIMIT #{pageSize} OFFSET #{offset}
