mapper.insert:
INSERT INTO document_approvers(doc_no, init_approver_no, init_approval_status, init_approval_datetime, init_reject_reason
							 , mid_approver_no, mid_approval_status, mid_approval_datetime, mid_reject_reason
							 , final_approver_no, final_approval_status, final_approval_datetime, final_reject_reason)
VALUES(#{docNo}, #{initApproverNo}, #{initApproverStatus}, #{initApproverDatetime}, #{initRejectReson}
	 , #{midApproverNo}, #{midApproverStatus}, #{midApproverDatetime}, #{midRejectReson}
	 , #{finalApproverNo}, #{finalApproverStatus}, #{finalApproverDatetime}, #{finalRejectReson});

mapper.select:
SELECT d.doc_no docNo, d.doc_title docTitle, d.doc_writer_no docWriterNo, e1.emp_name docWriterName, dp1.dept_name writerDeptName, d.doc_type docType, d.approval_status approvalStatus, d.doc_bin docBin, d.create_datetime updateDatetime, da.init_approver_no initApproverNo, e2.emp_name initApproversName, dp2.dept_name initApproversDeptName, da.init_approval_status initApprovalStatus, da.mid_approver_no midApproverNo, e3.emp_name midApproversName, dp3.dept_name midApproversDeptName, da.mid_approval_status midApprovalStatus, da.final_approver_no finalApproverNo, e4.emp_name finalApproversName, dp4.dept_name finalApproversDeptName, da.final_approval_status finalApprovalStatus, r1.rank_name docWriterRank, r2.rank_name initApproverRank, r3.rank_name midApproverRank, r4.rank_name finalApproverRank FROM document d INNER JOIN document_approvers da ON d.doc_no = da.doc_no INNER JOIN employee e1 ON e1.emp_no = d.doc_writer_no INNER JOIN employee e2 ON e2.emp_no = da.init_approver_no INNER JOIN employee e3 ON e3.emp_no = da.mid_approver_no INNER JOIN employee e4 ON e4.emp_no = da.final_approver_no INNER JOIN department dp1 ON e1.dept_code = dp1.dept_code INNER JOIN department dp2 ON e2.dept_code = dp2.dept_code INNER JOIN department dp3 ON e3.dept_code = dp3.dept_code INNER JOIN department dp4 ON e4.dept_code = dp4.dept_code INNER JOIN `rank` r1 ON e1.rank_no = r1.rank_no INNER JOIN `rank` r2 ON e2.rank_no = r2.rank_no INNER JOIN `rank` r3 ON e3.rank_no = r3.rank_no INNER JOIN `rank` r4 ON e4.rank_no = r4.rank_no
d.doc_bin = '0'
AND (d.doc_writer_no = #{empNo} OR da.init_approver_no = #{empNo} OR da.mid_approver_no = #{empNo} OR da.final_approver_no = #{empNo})
AND d.doc_writer_no = #{docWriterNo}
AND (
da.init_approver_no = #{initApproverNo}
OR da.mid_approver_no = #{midApproverNo}
OR da.final_approver_no = #{finalApproverNo}
)
AND d.approval_status = #{approvalStatus}
AND ( d.doc_no LIKE CONCAT('%', #{searchWord}, '%') OR d.doc_title LIKE CONCAT('%', #{searchWord}, '%') OR e1.emp_name LIKE CONCAT('%', #{searchWord}, '%') OR dp1.dept_name LIKE CONCAT('%', #{searchWord}, '%') OR e2.emp_name LIKE CONCAT('%', #{searchWord}, '%') OR dp2.dept_name LIKE CONCAT('%', #{searchWord}, '%') OR r1.rank_name LIKE CONCAT('%', #{searchWord}, '%') OR r2.rank_name LIKE CONCAT('%', #{searchWord}, '%')
OR d.approval_status = 'P'
OR d.approval_status = 'A'
OR d.approval_status = 'R'
OR d.approval_status = 'T'
)
ORDER BY d.create_datetime DESC LIMIT #{beginRow}, #{rowPerPage}

