mapper.update:
UPDATE pet_service_rsv 
SET rsv_status = #{rsvStatus}, rsv_no_show = #{rsvNoShow}
WHERE rsv_no = #{rsvNo}

mapper.select:
SELECT pr.rsv_no rsvNo, c.customer_name customerName, pr.service_no serviceNo, pr.rsv_datetime rsvDatetime, pr.rsv_status rsvStatus, pr.rsv_no_show rsvNoShow, ps.service_name serviceName, ps.service_desc serviceDesc FROM pet_service_rsv pr INNER JOIN pet_service ps ON pr.service_no = ps.service_no INNER JOIN customer c ON pr.customer_no = c.customer_no WHERE 1=1
AND (ps.service_name LIKE CONCAT('%', #{searchWord}, '%') OR ps.service_desc LIKE CONCAT('%', #{searchWord}, '%') OR c.customer_name LIKE CONCAT('%', #{searchWord}, '%'))
LIMIT #{pageSize} OFFSET #{offset}

SELECT service_no serviceNo, service_name serviceName, service_desc serviceDesc, service_price servicePrice FROM pet_service WHERE 1=1
AND (service_name LIKE CONCAT('%', #{searchWord}, '%') OR service_desc LIKE CONCAT('%', #{searchWord}, '%'))
LIMIT #{pageSize} OFFSET #{offset}
